import UIKit
import LocalAuthentication
import CDAlertView

class LockScreenViewController: BaseViewController, LockScreenViewModelDelegate  {
    var buttonArray = [UIButton]()
    var viewModel: LockScreenViewModel!
    
    @IBOutlet weak var backgroundImageView: UIImageView!
    @IBOutlet weak var PasscodeView: PasscodeView!
    @IBOutlet weak var OneButton: UIButton!
    @IBOutlet weak var TwoButton: UIButton!
    @IBOutlet weak var ThreeButton: UIButton!
    @IBOutlet weak var FourButton: UIButton!
    @IBOutlet weak var FiveButton: UIButton!
    @IBOutlet weak var SixButton: UIButton!
    @IBOutlet weak var SevenButton: UIButton!
    @IBOutlet weak var EightButton: UIButton!
    @IBOutlet weak var NineButton: UIButton!
    @IBOutlet weak var ZeroButton: UIButton!
    @IBOutlet weak var TitleLabel: UILabel!
    @IBOutlet weak var CancelButton: UIButton!
    @IBOutlet weak var TouchIDButton: UIButton!
    
    // MARK: Object lifecycle
    
    override func awakeFromNib() {
        super.awakeFromNib()

    }
    
    // MARK: View lifecycle
    
    override func viewDidLoad() {
        super.viewDidLoad()
        
        styleUI()
        PasscodeView.totalDotCount = 6

        if viewModel.passcodeState == .FirstStart {
            TitleLabel.text = NSLocalizedString("Create passcode for app", comment: "")
        } else if viewModel.passcodeState == .NotFirst {
            TitleLabel.text = NSLocalizedString("Enter passcode", comment: "")
        } else if viewModel.passcodeState == .RequirePass {
            TitleLabel.text = NSLocalizedString("Enter passcode", comment: "")
            TouchIDButton.setTitle(NSLocalizedString("Cancel", comment: ""), for: .normal)
        } else if viewModel.passcodeState == .ChangePass {
            TitleLabel.text = NSLocalizedString("Enter new passcode", comment: "")
            TouchIDButton.setTitle(NSLocalizedString("Cancel", comment: ""), for: .normal)
        }
    }
    
    // MARK: Event handling
    
    private func styleUI() {
        backgroundImageView.image = UIImage.init(named: "data-security-tips.jpg")
        blurImage()
        TitleLabel.font = UIFont.boldSystemFont(ofSize: 17 * kScale)

        buttonArray.append(ZeroButton)
        buttonArray.append(OneButton)
        buttonArray.append(TwoButton)
        buttonArray.append(ThreeButton)
        buttonArray.append(FourButton)
        buttonArray.append(FiveButton)
        buttonArray.append(SixButton)
        buttonArray.append(SevenButton)
        buttonArray.append(EightButton)
        buttonArray.append(NineButton)

        for button in buttonArray {
            self.styleButton(button: button, isScaleFontSize: true)
            button.tag = buttonArray.index(of: button)!
        }
//        styleButton(button: CancelButton, isScaleFontSize: false)
//        styleButton(button: TouchIDButton, isScaleFontSize: false)
    }
    
    func styleChangePassState() {
        viewModel.clearInput()
        viewModel.changePassState()
        TitleLabel.text = NSLocalizedString("Enter new passcode", comment: "")
        TouchIDButton.setTitle(NSLocalizedString("Cancel", comment: ""), for: .normal)
    }
    
    private func blurImage() {
        if !UIAccessibilityIsReduceTransparencyEnabled() {
            backgroundImageView.backgroundColor = UIColor.clear
            
            let blurEffect = UIBlurEffect(style: UIBlurEffectStyle.dark)
            let blurEffectView = UIVisualEffectView(effect: blurEffect)
            //always fill the view
            blurEffectView.frame = backgroundImageView.bounds
            blurEffectView.autoresizingMask = [.flexibleWidth, .flexibleHeight]
            
            backgroundImageView.addSubview(blurEffectView) //if you have more UIViews, use an insertSubview API to place it where needed
        }
    }
    
    private func styleButton(button: UIButton, isScaleFontSize: Bool) {
        if isScaleFontSize {
            button.titleLabel?.font = UIFont.boldSystemFont(ofSize: 20 * kScale)
        }
        button.layer.cornerRadius = 5.0;
        button.layer.borderColor = UIColor.white.cgColor
        button.layer.borderWidth = 1.0;
    }
    
    private func wrongPasscode() {
        PasscodeView.shakeAnimationWithCompletion {
            self.viewModel.clearInput()
            self.TitleLabel.text = NSLocalizedString("Wrong passcode. Try again", comment: "")
        }
    }
    
    // MARK: Navigation
    func navigateToHomeScreen() {
        if viewModel.passcodeState == .RequirePass {
            dismiss(animated: true, completion: { 
                NotificationCenter.default.post(name: Notification.Name(rawValue: Key.String.notiPerformSeguePasscodeView), object: nil)
            })
        } else if viewModel.passcodeState == .ChangePass {
            dismiss(animated: true, completion: {
                NotificationCenter.default.post(name: Notification.Name(rawValue: Key.String.notiAlertChangePassSuccess), object: nil)
            })
        
        } else {
            let appDelegate = UIApplication.shared.delegate as! AppDelegate
            navigationController?.pushViewController(appDelegate.tabBarController, animated: true)
        }
    }

    // MARK: Display logic
    
    
    // MARK: Selector logic
    @IBAction func clickedNumberButton(_ sender: Any) {
        let button = sender as! UIButton
        print("clicked \(button.tag) Button")
        viewModel.appendInputString(string: "\(button.tag)")
    }
    
    @IBAction func clickedCancelButton(_ sender: Any) {
        print("clicked Delete Button")
        if viewModel.passcodeState == .SecondInput && viewModel.inputDotCount == 0 {
            viewModel.resetInputString()
        } else {
            viewModel.deleteInputString(isFull: PasscodeView.isFull)
        }
    }
    
    @IBAction func clickedTouchIDButton(_ sender: Any) {
        if viewModel.passcodeState == .ChangePass || viewModel.passcodeState == .RequirePass {
            dismiss(animated: true, completion: nil)
            return
        }
        
        if viewModel.passcodeState == .FirstStart {
            let alert = CDAlertView(title: nil, message: NSLocalizedString("Set passcode and enable TouchID", comment: ""), type: CDAlertViewType.warning)
            alert.show()
            return
        }
        
        let enableTouchID = UserDefaults.standard.bool(forKey: Key.UserDefaults.enableTouchID)
        if !enableTouchID {
            let alert = CDAlertView(title: nil, message: NSLocalizedString("Go Setting page and enable TouchID", comment: ""), type: CDAlertViewType.warning)
            alert.show()
            return
        }
        
        let context = LAContext()
        var error: NSError?
        let reasonString = NSLocalizedString("Authentication is needed", comment: "")
        
        if context.canEvaluatePolicy(.deviceOwnerAuthenticationWithBiometrics, error: &error) {
            context.evaluatePolicy(.deviceOwnerAuthenticationWithBiometrics, localizedReason: reasonString, reply: { (success, evalPolicyError) in
                if success {
                    self.TitleLabel.text = NSLocalizedString("Enter passcode", comment: "")
                    self.viewModel.clearInput()
                    self.navigateToHomeScreen()
                } else {
                    print("\(evalPolicyError?.localizedDescription ?? "")")
                    
                    var message : String
                    var showAlert : Bool
                    
                    switch evalPolicyError!._code {
                    case LAError.authenticationFailed.rawValue:
                        print("Authentication has a problem verifying your identity.")
                        message = NSLocalizedString("Problem verifying identity", comment: "")
                        showAlert = true
                        
                    case LAError.systemCancel.rawValue:
                        print("Authentication was cancelled by the system")
                        message = NSLocalizedString("Cancelled by the system", comment: "")
                        showAlert = true
                        
                    case LAError.userCancel.rawValue:
                        print("Authentication was cancelled by the user")
                        message = NSLocalizedString("Cancelled by user", comment: "")
                        showAlert = true
                        
                    case LAError.userFallback.rawValue:
                        print("User selected to enter custom passcode")
                        message = NSLocalizedString("User pressed passcode", comment: "")
                        showAlert = true
                        
                    default:
                        print("Authentication failed")
                        message = NSLocalizedString("TouchID not be configured", comment: "")
                        showAlert = true
                    }
                    
                    let alertView = UIAlertController(title: NSLocalizedString("Error", comment: ""), message: message, preferredStyle:.alert)
                    let okAction = UIAlertAction(title: NSLocalizedString("OK", comment: ""), style: .default, handler: nil)
                    alertView.addAction(okAction)
                    if showAlert {
                        self.present(alertView, animated: true, completion: nil)
                    }
                }
            })
        } else {
            // 5.
            let alertView = UIAlertController(title: NSLocalizedString("Error", comment: ""), message: NSLocalizedString("TouchID not available", comment: ""), preferredStyle:.alert)
            let okAction = UIAlertAction(title: NSLocalizedString("OK", comment: ""), style: .default, handler: nil)
            alertView.addAction(okAction)
            present(alertView, animated: true, completion: nil)
        }
    }
    

    // MARK: LockScreenViewModelDelegate
    func validationSuccess() {
        TitleLabel.text = NSLocalizedString("Enter passcode", comment: "")
        viewModel.clearInput()
        navigateToHomeScreen()
    }
    
    func validationFail() {
        wrongPasscode()
    }
    
    func setInputDotCount(inputDotCount: Int) {
        PasscodeView.inputDotCount = inputDotCount
    }
    
    func setTitleLabel(text: String) {
        TitleLabel.text = text
    } 
    
    func setTitleButton(text: String) {
        CancelButton.setTitle(text, for: .normal)
    }
}


